import React, { useState, useEffect } from "react";
import Details from './Details'
import Files from "./Files";
import { Container, Row, Col, Button, Card, Form } from 'react-bootstrap'

const Commits = ({ url, user, repo, branch, getReady, token }) => {
    const [commits, setCommits] = useState('')

    useEffect(() => {
        (async() => {
            const result = await fetch(url, {
                headers: { Authorization: token }
            })
	        const repos = await result.json()

            setCommits(repos)
        })()
    }, [url, token])

    const getReady2 = (state) => (e) => {
        e.preventDefault()
        setReady2(state)
    }

    const showCommits = () => {
        return (
            <Card>
                <Card.Body>
                {commits.map((commit, index) => {
                    return <Card.Text key = { index}>{commit.commit.message}</Card.Text>
                    })}
                    </Card.Body>
            </Card>
        )
    }
    return(
        <div>
            {commits.length > 0 ? 
            (<div >
                    <Card bg="primary" text="white">
                        <Card.Header><br/><h2>Commits in { repo }</h2><br/></Card.Header>
                    </Card>
                    {showCommits()}
                    <Details user = { user } repo = { repo } branch = { branch } token = { token } url = { commits[0].url.replace('commits', 'git/trees') + "?recursive=1" }/>
                    <Button onClick = { getReady2(true)}>Show files</Button>
            </div> 
            )
            :
            <p>Couldn't find any repository :(</p>
            }
            <button onClick = { getReady("", "", "", true)}>Back</button>
        </div>
    )
}


export default Commits
