import React, { useState } from "react";
import Repository from "./Repository";



const Repositories = props => {
    const [commits, setCommits] = useState([])

    const getCommits = (repo) => async() => {
	    const result = await fetch(`${repo}`)
	    let response = await result.json()
        const newState = [commits, response];
        setCommits(newState)

        console.log(commits)
    }
    return(
        <div>
        { props.repositories.map((repository , index) => {
            return (
            <div key= { index }>
                <h2> { repository.name }</h2>
                <button onClick = { getCommits(repository.commits_url.slice(0, -6)) } >See commits</button>
                <a href= { repository.link } >See on Github</a>
            </div>
            )})
        }
        </div>
    )
}


export default Repositories
