import { render } from '@testing-library/react';
import React, { Component, useState, useEffect } from 'react';
import { Container, Row, Col, Button, Card, Form, Input } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'

import './App.css';
import User from './components/User';

const App = () => {
	const [user, setUser] = useState('')
	const [ready, setReady] = useState(false)

	const getUser = (e) => {
		setReady(false)
		setUser(e.target.value)
	}

	const getReady = () => {
		if (user !== '')
			setReady(true)
	}

	return (
	  <div className="App">
		<header className="App-header">
		  <h1 className="App-title">Github API REST</h1>
		</header>
		<Form>
		<Form.Group className="mb-3" controlId="formBasicEmail">
			<Form.Label>Email address</Form.Label>
			<Form.Control value={user} onChange={ getUser }type="text" placeholder="Enter user" />
			<Form.Text className="text-muted">
			We'll never share your email with anyone else.
			</Form.Text>
		</Form.Group>
        <input  name="userName"></input>
		<Button onClick = { getReady }>Search</Button>
		</Form>
		<h1>Welcome { user }</h1>
		{ ready ? <User user = { user }/>
		:
		<p>Busca un usuario para encontrar sus repositorios</p> }
	  </div>

	)

}

export default App;
