import { render } from '@testing-library/react';
import React, { Component, useState, useEffect } from 'react';
import { Container, Row, Col, Button, Card, Form, FormControl, InputGroup, Nav } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'

import './App.css';
import User from './components/User';

const App = () => {
	const [user, setUser] = useState('')
	const [ready, setReady] = useState(false)

	const getUser = (e) => {
		setReady(false)
		setUser(e.target.value)
	}

	const getReady = () => {
		if (user !== '')
			setReady(true)
	}

	return (
	  <div className="App">
		<Nav
		activeKey="/" justify variant="tabs" className="justify-content-center"
		onSelect={(selectedKey) => alert(`selected ${selectedKey}`)}
		>
		<Nav.Item>
			<Nav.Link href="/">Home</Nav.Link>
		</Nav.Item>
		<Nav.Item>
		<Form>
			<Row className="align-items-center">
				<Col m={3} className="my-1">
					<Form.Label htmlFor="inlineFormInputInput" visuallyHidden>
						Name
					</Form.Label>
					<Form.Control value={user} onChange={ getUser }type="text" placeholder="Enter user" />
				</Col>
				<Col xs="auto" className="my-1">
				<Button onClick = { getReady }>Search</Button>
				</Col>
			</Row>
		</Form>
		</Nav.Item>
		</Nav>
		<Container fluid>
		<header className="App-header">
		  <h1 className="App-title">Welcome { user }</h1>
		</header>
		{ ready ? <User user = { user }/>
		:
		<p>Busca un usuario para encontrar sus repositorios</p> }
		</Container>
	  </div>

	)

}

export default App;
