import { getSuggestedQuery } from "@testing-library/react";
import React, { useState, useEffect } from "react";
import Lines from './Lines'
import { Container, Row, Col, Button, Card, Form } from 'react-bootstrap'

const Details = ({ url, user, repo, branch }) => {
    const [content, setContent] = useState('')
    const [num, setNum] = useState('')

    useEffect(() => {
        (async() => {
            const result = await fetch(url, {
                headers: { Authorization: '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' }
            })
	        const contents = await result.json()

            setContent(contents)
        })()
    }, [url])

    const getType = (file) => {
        if (file.type === 'blob')
            return <Card.Header>{ file.path }</Card.Header>
        else
            return <Card.Header> { file.path }</Card.Header>
    }

    const getCount = () => {
        let lang = {}
        let ext
        let files = content.tree

        for (let i = 0; i < files.length; i++) {
            if (files[i].type !== 'tree') {
                if (files[i].path.lastIndexOf('.') !== -1)
                    ext = files[i].path.substr(files[i].path.lastIndexOf('.'))
                else
                    ext = files[i].path
                if (ext.lastIndexOf('/') !== -1)
                ext = ext.substr(ext.lastIndexOf('/'))
                if (lang[ext] !== undefined)
                    lang[ext] += 1
                else
                    lang[ext] = 1
            }
        }
        return lang
    }

    const getUrl = (file) => { 
        let url1 = "https://raw.githubusercontent.com/" + user + "/" + repo + "/" + branch + "/" + file.path
        return url1
    }

    const showFilesCount = () => {
        return (
            <Card >
                <Card.Header>El n√∫mero de archivos es:</Card.Header>
                 { Object.entries(getCount()).map(([key, value]) => {
                    return (
                        <Card.Text key={key}>{key} : {value}</Card.Text>
                    )
                }) }
            </Card>
        )
    }
    const showFiles = () => {
        return (
            <Container>
            {content.tree.map((file, index) => {
                const color = (file.type === 'tree' ? 'info' : 'light')
                return (
                <Card key = { index }>
                    <Card.Header bg={ color }>{ getType(file) }</Card.Header>
                    <Card.Text>{ showLines(file)}</Card.Text>
                </Card> )})}
            </Container>
        )

    }
    const showLines = (file) => {
        if (file.type === 'blob')
            return <Lines url = { getUrl(file) } repo = { repo }/>
    }
    return(

        <Container>

            {typeof(content) === 'object' ?
            (<div >
                { showFilesCount() }
                { showFiles() }
            </div> 
            )
            :
            <p>El repositorio no tiene contenido</p>
            }
        </Container>
    )
    }

export default Details
