import { render } from '@testing-library/react';
import React, { Component, useState, useEffect } from 'react';
import './App.css';

import Form from './components/Form';
import User from './components/User';

const App = () => {
	const [user, setUser] = useState('')
	const [ready, setReady] = useState(false)

	const getUser = (e) => {
		setUser(e.target.value)
		getReady()
	}

	const getReady = () => {
		if (user !== '')
			setReady(true)
	}

	return (
	  <div className="App">
		<header className="App-header">
		  <h1 className="App-title">Github API REST</h1>
		</header>
        <input onChange = { getUser } value={user} type="text" name="userName"></input>
		<h1>Welcome { user }</h1>
		{ ready ? <User user = { user }/>
		:
		<p>Busca un usuario para encontrar sus repositorios</p> }
	  </div>

	)

}

export default App;
