import React, { useState, useEffect } from "react";
import Repos from "./Repos";



const Repositories = ({ user }) => {
    const [repositories, setRepositories] = useState([])
    const [ready, setReady] = useState(true)
    const [url, setUrl] = useState('')

    useEffect(() => {
        (async() => {
            const result = await fetch(`https://api.github.com/users/${user}/repos`)
	        const repos = await result.json()
            setRepositories(repos)
        })()
    }, [user])

    const getReady = (commit_url) => (e) => {
		e.preventDefault()
		setReady(false)
        setUrl(commit_url)
	}
    return(

        <div>
            {console.log(ready)}
            {ready ? 
            (<div>
                 <h1>Welcome { user }</h1>
                <h3>Tus repositorios son:</h3>
                {repositories.length > 0 ? 
                (<div>
                    {repositories.map(repository => 
                        <div>
                            <h4>{repository.name}</h4>
                            <a href = {repository.html_url}>See on Github</a>
                            <button onClick= { getReady(repository.commits_url.slice(0, -6)) } >See commits!</button>
                            {console.log(url)}
                        </div>)}
                </div> 
                )
                :
                <p>El usuario no tiene repositorios</p>
                }
            </div> 
            )
            :
            <Repos url = { url }/>
            }





        </div>
    )
}


export default Repositories
