import React, { useState } from "react";
import { useNavigate } from 'react-router-dom'



function Form(props) {
    let [repositories, getRepositories] = useState(0);

    let navigate = useNavigate();
    return (
    <form onSubmit = { props.getUser }>
        <input type="text" name="userName"></input>
        <button onClick = {() => {
            getRepositories = async (e) => {
                const userName = e.target.elements.userName.value;
                e.preventDefault()
                const apiURL = await fetch(`https://api.github.com/users/${userName}/repos`);

                const data = await apiURL.json();
                repositories = data;
                console.log(data)
            }




            navigate('/user', { props })
        }}>Search</button>
    </form>
    )
}
export default Form
