import React, { useState, useEffect } from "react";


const Details = ({ url }) => {
    const [content, setContent] = useState('')
    const [num, setNum] = useState([{ 'lang': '.c'}])

    useEffect(() => {
        (async() => {
            const result = await fetch(url)
	        const contents = await result.json()

            setContent(contents)
        })()
    }, [url])

    const getType = (file) => {
        let ext
        console.log(file.lastIndexOf('.'))
        if (file.lastIndexOf('.') !== -1)
            ext = file.substr(file.lastIndexOf('.'))
        else
            ext = file
        for (let lang of num) {
            if (lang.contains(ext))
                lang.ext++
            else
                setNum((prev) => [...prev, {lang: 0}])

        }
        console.log(num)
        return <h4> { file }</h4>

    }

    return(
        <div>
            <h3>Los archivos son:</h3>
            {typeof(content) === 'object' ? 
            (<div >
                {content.tree.map((file, index) => {
                    return (
                    <div key = { index }>
                        { getType(file.path) }
                    </div> )})}
            </div> 
            )
            :
            <p>El repositorio no tiene contenido</p>
            }
        </div>
    )
    }

export default Details
