import React, { useState, useEffect } from "react";
import Files from './Files'
import { Button, Card } from 'react-bootstrap'

const Details = ({ url, user, repo, branch }) => {
    const [content, setContent] = useState('')
    const [ready2, setReady2] = useState(true)

    useEffect(() => {
        (async() => {
            const result = await fetch(url, {
                headers: { Authorization: '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' }
            })
	        const contents = await result.json()

            setContent(contents)
        })()
    }, [url])

    const getReady2 = (state) => (e) => {
        e.preventDefault()
        setReady2(state)
    }

    const getCount = () => {
        let lang = {}
        let ext
        let files = content.tree

        for (let i = 0; i < files.length; i++) {
            if (files[i].type !== 'tree') {
                if (files[i].path.lastIndexOf('.') !== -1)
                    ext = files[i].path.substr(files[i].path.lastIndexOf('.'))
                else
                    ext = files[i].path
                if (ext.lastIndexOf('/') !== -1)
                ext = ext.substr(ext.lastIndexOf('/'))
                if (lang[ext] !== undefined)
                    lang[ext] += 1
                else
                    lang[ext] = 1
            }
        }
        return lang
    }


    const showFilesCount = () => {
        return (
            <div>
            <Card >
                 { Object.entries(getCount()).map(([key, value]) => {
                    return (
                        <div key={key}><Card.Text>{key} : {value}</Card.Text></div>
                    )
                }) }
            </Card>
            </div>
        )
    }

    const show = () => {
        if (ready2) {
            return showFilesCount()
        } else 
            return <Files content = { content } repo = { repo } user = { user } branch = { branch } getReady2 = { getReady2 }></Files>
    }
    return(

        <div>
            {typeof(content) === 'object' ?
            (<div >
                <Card bg="primary" text="white">
                <Card.Header><br/><h2>Number of files: { repo }</h2><br/></Card.Header>
                <Card.Body><Button variant="light" onClick = { getReady2(false)}>Show files</Button></Card.Body>
                </Card>
                <div>{ show() }</div>
            </div> 
            )
            :
            <p>El repositorio no tiene contenido</p>
            }
        </div>
    )
    }

export default Details
