import { render } from '@testing-library/react';
import React, { Component, useState, useEffect } from 'react';
import { Container, Row, Col, Button, Card, Form, FormControl, InputGroup, Nav, Alert } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'

import './App.css';
import User from './components/User';

const App = () => {
	const [user, setUser] = useState('')
	const [ready, setReady] = useState(false)

	const getUser = (e) => {
		e.preventDefault()
		setReady(false)
		setUser(e.target.value)
	}

	const getReady = (e) => {
		if (user !== '')
			setReady(true)

	}

	const preventForm = (e) => {
		e.preventDefault()

	}
	return (
	  <div className="App">
		<Nav
		py={5}
		activeKey="/" justify variant="tabs" className="justify-content-center"
		onSelect={(selectedKey) => alert(`selected ${selectedKey}`)}
		>
		<Nav.Item>
		<Form onSubmit = { preventForm }> 
			<Row className="align-items-center">
				<Col m={3} className="my-1">
					<Form.Label htmlFor="inlineFormInputInput" visuallyHidden>
						Name
					</Form.Label>
					<Form.Control value={user} onChange={ getUser }type="text" placeholder="Enter user" />
				</Col>
				<Col xs="auto" className="my-1">
				<Button onClick = { getReady } onKeyPress = { getReady }>Search</Button>
				</Col>
			</Row>
		</Form>
		</Nav.Item>
		</Nav>

		{ ready ? <div>
		<Alert variant="success">
		<Alert.Heading>Thanks { user }!</Alert.Heading>
  		<p>
   		Use the search box again to change user or navigate with the buttons!
  		</p>
		</Alert>
		<User user = { user }/>
		</div>
		:
		<Alert variant="primary">
		<Alert.Heading>Welcome!</Alert.Heading>
  		<p>
   		Search your user in the input box and we will show all the repositories, commits and files in it
  		</p>
		</Alert>
		}
	  </div>

	)

}

export default App;
