import { render } from '@testing-library/react';
import React, { Component } from 'react';
import './App.css';

import Form from './components/Form';
import Repository from './components/Repository'
import Repositories from './components/Repositories';

class App extends Component {
  state = {
	repositories: [],
	commits: []
  }

  getUser = async (e) => {
	const userName = e.target.elements.userName.value;
	e.preventDefault()
	fetch(`https://api.github.com/users/${userName}/repos`)
    .then(response => response.json())
    .then(data => {
		data.map(repository => {
			const prop = {
				"name": repository.name,
				"link": repository.html_url,
				"commitsURL": repository.commits_url,
				"languages": repository.languages_url
			}
			this.setState( prevState => ({
					repositories: [...prevState.repositories,  prop]
				}));
		})
		console.log(this.state.repositories)
    })
	/*
	fetch(``)
    .then(response => response.json())
    .then(commits => {
        this.setState( { commits: commits });
    })
	*/
  }

  getCommits = async (e) => {

	/*
	const user = this.repository.owner.login
	const repo = this.repository.name
	e.preventDefault()
	const url = `https://api.github.com/repos/${user}/${repo}/commits`
	const commitsURL = await fetch(url);
	const data = await commitsURL.json();
	console.log(data)
	this.setState( { repositories: data });
	*/
}

  render() {
	return (
	  <div className="App">
		<header className="App-header">
		  <h1 className="App-title">Github API REST</h1>
		</header>
		<Form getUser = { this.getUser }/>
		<Repositories repositories= { this.state.repositories } getCommits = { this.getCommits }/>

	  </div>
	)
  };
}

export default App;
