import React, { useState, useEffect } from "react";
import { Card } from "react-bootstrap";
import Lines from './Lines'

const Files = ({ content, repo, user, branch }) => {
    const [file, setFile] = useState('')

    const showFiles = () => {
        return (
            <div>
            {content.tree.map((file, index) => {
                const color = (file.type === 'tree' ? 'primary' : 'light')
                const textColor = (file.type === 'tree' ? 'white' : 'dark')
                return (
                <Card bg={ color } text={textColor}>
                    <Card.Header>{ file.path }</Card.Header>
                    <Card.Text>{ showLines(file)}</Card.Text>
                </Card>
              )})}
              </div>
        )

    }

    const showLines = (file) => {
        if (file.type === 'blob')
            return <Lines url = { getUrl(file) } repo = { repo }/>
    }

    const getUrl = (file) => { 
        let url1 = "https://raw.githubusercontent.com/" + user + "/" + repo + "/" + branch + "/" + file.path
        return url1
    }

    return(
        (<div>
            { file.length > 0 ?
                <Card.Text>{ (file.match(/\n/g) || '').length } líneas</Card.Text>
                :
                <h4>No se pudieron contar las líneas</h4>
            }
        </div>)
    )
}


export default Files
