import { render } from '@testing-library/react';
import React, { Component, useState, useEffect } from 'react';
import { Container, Row, Col, Button, Card, Form, FormControl, InputGroup } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'

import './App.css';
import User from './components/User';

const App = () => {
	const [user, setUser] = useState('')
	const [ready, setReady] = useState(false)

	const getUser = (e) => {
		setReady(false)
		setUser(e.target.value)
	}

	const getReady = () => {
		if (user !== '')
			setReady(true)
	}

	return (
	  <div className="App">
		<header className="App-header">
		  <h1 className="App-title">Github API REST</h1>
		</header>
		<Form>
		<Row className="align-items-center">
			<Col sm={3} className="my-1">
				<Form.Label htmlFor="inlineFormInputInput" visuallyHidden>
					Name
				</Form.Label>
				<Form.Control value={user} onChange={ getUser }type="text" placeholder="Enter user" />
			</Col>
			<Col xs="auto" className="my-1">
			<Button type="submit">Submit</Button>
			</Col>
		</Row>
		</Form>
		<Form>
		<Form.Group className="mb-3" controlId="formUser">
			<InputGroup>

			</InputGroup>
			<Button onClick = { getReady }>Search</Button>
		</Form.Group>

		</Form>
		<h1>Welcome { user }</h1>
		{ ready ? <User user = { user }/>
		:
		<p>Busca un usuario para encontrar sus repositorios</p> }
	  </div>

	)

}

export default App;
