import { render } from '@testing-library/react';
import React, { Component } from 'react';
import {Redirect} from 'react-router-dom'
import './App.css';

import Form from './components/Form';
import Repositories from './components/Repositories';

class App extends Component {
  state = {
	repositories: [],
	redirect: false
  }

  getUser = async (e) => {
	const userName = e.target.elements.userName.value;
	e.preventDefault()
	const apiURL = await fetch(`https://api.github.com/users/${userName}/repos`);

	const data = await apiURL.json();
	this.setState( { repositories: data, redirect: true });
	console.log(this.state.repositories[0])
  }

  render() {
	const { redirect } = this.state.redirect;
	if (redirect)
		return  <Redirect to='/user'/>;
	return (
	  <div className="App">
		<header className="App-header">
		  <h1 className="App-title">Github API REST</h1>
		</header>

		<Form getUser = { this.getUser }/>
		<Repositories repositories= { this.state.repositories }/>

	  </div>
	)
  };
}

export default App;
