import { getSuggestedQuery } from "@testing-library/react";
import React, { useState, useEffect } from "react";
import Lines from './Lines'

const Details = ({ url, getReady2, getReady3, user, repo }) => {
    const [content, setContent] = useState('')
    const [num, setNum] = useState('')

    useEffect(() => {
        (async() => {
            const result = await fetch(url)
	        const contents = await result.json()

            setContent(contents)
        })()
    }, [url])

    const getType = (file) => {
        if (file.type === 'blob')
            return <h4>{ file.path }</h4>
        else
            return <h1> { file.path }</h1>
    }

    const getCount = () => {
        let lang = {}
        let ext
        let files = content.tree

        for (let i = 0; i < files.length; i++) {
            if (files[i].type !== 'tree') {
                if (files[i].path.lastIndexOf('.') !== -1)
                    ext = files[i].path.substr(files[i].path.lastIndexOf('.'))
                else
                    ext = files[i].path
                if (ext.lastIndexOf('/') !== -1)
                ext = ext.substr(ext.lastIndexOf('/'))
                if (lang[ext] !== undefined)
                    lang[ext] += 1
                else
                    lang[ext] = 1
            }
        }
        return lang
    }

    const getUrl = (file) => { 
        let url1 = "https://raw.githubusercontent.com/" + user + "/" + repo + "/main/" + file.path
        if (file.type === 'blob')
            return url1
    }

    const show = (file) => {
        if (file.type === 'blob')
            return  <button onClick= { getReady3(getUrl(file), false) } >Show file lines</button>
    }
    return(

        <div>
            <h3>El n√∫mero de archivos es:</h3>
            {typeof(content) === 'object' ? 
            (<div >
                { Object.entries(getCount()).map(([key, value]) => {
                    return (
                        <h4 key={key}>{key} : {value}</h4>
                    )
                }) }

                {content.tree.map((file, index) => {
                    return (
                    <div key = { index }>
                        { getType(file) }
                        <Lines url = { getUrl(file) } repo = { repo } getReady3 = { getReady3 }/>
                        { show(file) }
                    </div> )})}
                    <button onClick = { getReady2("", true)}>Back</button>

            </div> 
            )
            :
            <p>El repositorio no tiene contenido</p>
            }
        </div>
    )
    }

export default Details
