import { render } from '@testing-library/react';
import React, { Component, useState, useEffect } from 'react';
import './App.css';

import Form from './components/Form';
import User from './components/User';

const App = () => {
	const [user, setUser] = useState('')
	const [ready, setReady] = useState(false)

	const getUser = (e) => {
		setUser(e.target.value)
	}

	const getReady = (e) => {
		e.preventDefault()
		if (user !== '')
			setReady(true)
	}

	const clearUser = () => {
		setUser = ''
	}

	return (
	  <div className="App">
		<header className="App-header">
		  <h1 className="App-title">Github API REST</h1>
		</header>
		<form onSubmit = { setUser }>
        	<input onChange = { getUser }type="text" name="userName"></input>
        <button onClick= { getReady }>Search</button>
    	</form>
		{ ready ? <User user = { user } clearUser = { user }/>
		:
		<p></p> }
	  </div>

	)

}

export default App;
