import React, { useState, useEffect } from "react";


const Details = ({ url }) => {
    const [content, setContent] = useState('')
    const [dir_content, setDir] = useState('')

    useEffect(() => {
        (async() => {
            const result = await fetch(url)
	        const contents = await result.json()

            setContent(contents)
        })()
    }, [url])

    const getFiles = (dir_url) =>  async () => {
        const result = await fetch(dir_url)
	    const contents = await result.json()
        setDir(contents)
    }

    const getType = (file) => {
        if (file.type === "dir")
        {
            getFiles(file.url)
            if (dir_content.length > 0)
                getContents()
            console.log(dir_content)
            return <h4><a href= { file.url }> { file.name }</a></h4>
        }
        else
            return <h4> { file.name }</h4>
    }

    const getContents = () => {
        return (
            <div >
                {dir_content.map((file, index) => {
                    return (
                    <div key = { index }>
                        { getType(file)}
                    </div> )})}
            </div> 
        )
    }

    return(
        <div>
            <h3>Los content son:</h3>
            {content.length > 0 ? 
            (<div >
                {content.map((file, index) => {
                    console.log(dir_content)
                    return (
                    <div key = { index }>
                        { getType(file)}
                    </div> )})}
            </div> 
            )
            :
            <p>El usuario no tiene repositorios</p>
            }
        </div>
    )
    }

export default Details
