import React, { useState, useEffect } from "react";
import Commits from "./Commits";
import Repositories from "./Repositories";
import Details from "./Details";
import Lines from "./Lines"


const User = ({ user, clearUser }) => {
    const [ready, setReady] = useState(true)
    const [ready2, setReady2] = useState(true)
    const [ready3, setReady3] = useState(true)
    const [repo, setRepo] = useState('')
    const [url, setUrl] = useState('')
    const [tree_sha, setTree] = useState('')
    const [lines, setLines] = useState('')

    const getReady = (commit_url, repo, state) => (e) => {
		e.preventDefault()
		setReady(state)
        if (commit_url !== '')
            setUrl(commit_url)
        if (repo !== '')
            setRepo(repo)
	}

    const getReady2 = (tree_sha, state) => (e) => {
        e.preventDefault()
		setReady2(state)
        if (tree_sha !== '')
            setTree(tree_sha)
    }

    const getReady3 = (url, state) => (e) => {
        setLines(url)
        setReady3(state)
    }

    const show = () => {
        if (ready)
            return <div><Repositories user = { user } getReady = { getReadyÂ }/></div>
        else
            if (ready2)
                return <Commits url = { url } getReady = { getReady } getReady2 =  {getReady2} />
            else
                if (ready3)
                    return <Details user = { user } repo = { repo } url = { tree_sha } getReady2 = { getReady2} getReady3 = { getReady3 }/>
                else
                    return <Lines url = { lines } repo = { repo } getReady3 = { getReady3 }/>
    }
    return(
        <div>
            { clearUser() }
            { show() }
        </div>
    )
}


export default User
