import React from "react";
import Repository from "./Repository";



const Repositories = props => (
    <React.Fragment>
    { props.repositories.map((repository , index) => {
        console.log(props)
        console.log("Here: " + repository.name)
        const getCommits = async () => {
            const user = repository.owner.login
            const repo = repository.name
            const url = `https://api.github.com/repos/${user}/${repo}/commits`
            const commitsURL = await fetch(url);
            const data = await commitsURL.json();
            console.log(data)
            this.setState( { repositories: data });
        }

        return (
            <React.Fragment key= { index }>
                <p> { repository.name }</p>
                <a href= { repository.html_url } >See on Github</a>
                < Repository />
            </React.Fragment>
        )
    })
    }
    </React.Fragment>
)


export default Repositories
