import React, { useState, useEffect } from "react";


const Details = ({ url }) => {
    const [content, setContent] = useState('')
    const [dir_content, setDir] = useState('')

    useEffect(() => {
        (async() => {
            const result = await fetch(url)
	        const contents = await result.json()

            setContent(contents)
        })()
    }, [url])

    const getFiles = (dir_url) =>  async () => {
        const result = await fetch(dir_url)
	    const contents = await result.json()

        setDir(contents)
    }

    const getType = (file) => {
        if (file.type === "dir")
        {
            const dir = getFiles(file.url)
            console.log(dir)
            getContents(getFiles(file.url))
            return <h4><a href= { file.url }> { file.name }</a></h4>
        }
        else
            return <h4> { file.name }</h4>
    }

    const getContents = (dir_cont) => {
        console.log(dir_cont)
        return (

            <p></p>
        )
    }

    return(
        <div>
            <h3>Los content son:</h3>
            {content.length > 0 ? 
            (<div >
                {content.map((file, index) => {
                    return (
                    <div key = { index }>
                        { getType(file)}
                    </div> )})}
            </div> 
            )
            :
            <p>El usuario no tiene repositorios</p>
            }
        </div>
    )
    }

export default Details
