import React, { useState, useEffect } from "react";
import Repository from "./Repository";



const Repositories = ({ user }) => {
    const [repositories, setRepositories] = useState([])
    const [ready, setReady] = useState(false)

    useEffect(() => {
        (async() => {
            const result = await fetch(`https://api.github.com/users/${user}/repos`)
	        const repos = await result.json()
            setRepositories(repos)
        })()
    }, [user])

    const getReady = (e) => {
		e.preventDefault()
		setReady(true)
	}
    if (ready)
        return (
            <div></div>
        )
    return(
        <div>
            <h1>Welcome { user }</h1>
            <h3>Tus repositorios son:</h3>
            {repositories.length > 0 ? 
            (<div>
                {repositories.map(repository => 
                    <div>
                        <h4>{repository.name}</h4>
                        <a href = {repository.html_url}>See on Github</a>
                        <button onClick= { getReady }>See commits!</button>
                    </div>)}
            </div> 
            )
            :
            <p>El usuario no tiene repositorios</p>
            }
        </div>
    )
}


export default Repositories
