import React, { useState, useEffect } from "react";

const Repositories = ({ user, getReady }) => {
    const [repositories, setRepositories] = useState([])
    const [token] = useState('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')

    useEffect(() => {
        (async() => {
            const result = await fetch(`https://api.github.com/users/${user}/repos`)
	        const repos = await result.json()

            setRepositories(repos)
        })()
    }, [user])

    return(
        <div>
            <h3>Tus repositorios son:</h3>
                {repositories.length > 0 ? 
                (<div>
                    {repositories.map((repository, index) => { 
                        return (
                            <div key = { index }>
                                <h4>{repository.name}</h4>
                                <a href = {repository.html_url}>See on Github</a>
                                <button onClick= { getReady(repository.commits_url.slice(0, -6), repository.name, repository.default_branch, false) } >See commits!</button>
                            </div>
                        )
                    })}
                </div>)
                :
                <p>El usuario no tiene repositorios</p>
                }
            </div> 
            )
    }


export default Repositories
