import React, { useState, useEffect } from "react";
import { Container, Row, Col, Button, Card, Form } from 'react-bootstrap'

const Repositories = ({ user, getReady, token }) => {
    const [repositories, setRepositories] = useState([])

    useEffect(() => {
        (async() => {
            const result = await fetch(`https://api.github.com/users/${user}/repos`, {
                headers: { Authorization: token }
            })
	        const repos = await result.json()

            setRepositories(repos)
        })()
    }, [user, token])

    return(
        <div>
            <h3>Tus repositorios son:</h3>
                {repositories.length > 0 ? 
                (<div className="repositories">
                    {repositories.map((repository, index) => { 
                        return (
                            <Card style={{ width: '50%' }} key = { index } className="repo">
                                <Card.Body>
                                <Card.Title  className="repo title">{repository.name}</Card.Title>
                                <Button variant="secondary" className="repo-link" href = {repository.html_url}>See on Github</Button>
                                <Button className="repo-button" onClick= { getReady(repository.commits_url.slice(0, -6), repository.name, repository.default_branch, false) } >See commits!</Button>
                                </Card.Body>
                            </Card>
                        )
                    })}
                </div>)
                :
                <p>El usuario no tiene repositorios</p>
                }
            </div> 
            )
    }


export default Repositories
