import { render } from '@testing-library/react';
import React, { Component } from 'react';
import './App.css';

import Form from './components/Form';
import Repositories from './components/Repositories';

class App extends Component {
  state = {
	repositories: [],
  }

  getUser = async (e) => {
	const userName = e.target.elements.userName.value;
	e.preventDefault()
	const result = await fetch(`https://api.github.com/users/${userName}/repos`)
	.then(response => response.json())
	.then(data => {
		const com = {}, arr = []
		data.forEach(repository => {
			const result = fetch(`${repository.commits_url.slice(0, -6)}`)
			.then(response => response.json())
			.then(data => {
				data.forEach(commit => {
					com.html_url = commit.html_url
					com.url = commit.url
					arr.push(com)
				})
			})

			this.setState(prevState => ({
					repositories: [...prevState.repositories,  {
						"name": repository.name,
						"link": repository.html_url,
						"languages": repository.languages_url,
						"commits": arr
					}]
				}));
		})
	})
  }

  render() {
	return (
	  <div className="App">
		<header className="App-header">
		  <h1 className="App-title">Github API REST</h1>
		</header>
		<Form getUser = { this.getUser }/>
		{ console.log(this.repositories)}
		<Repositories repositories= { this.state.repositories }/>

	  </div>
	)
  };
}

export default App;
